mapper.insert:
insert into firstindex (fid, fname, fweight
  )
values (#{fid,jdbcType=INTEGER}, #{fname,jdbcType=VARCHAR}, #{fweight,jdbcType=DOUBLE}
  )

mapper.update:
update firstindex
set fname = #{fname,jdbcType=VARCHAR},
  fweight = #{fweight,jdbcType=DOUBLE}
where fid = #{fid,jdbcType=INTEGER}

mapper.select:
select fid, fname, fweight
from firstindex
where fid = #{fid,jdbcType=INTEGER}
