mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  admin = #{admin,jdbcType=INTEGER},
  state = #{state,jdbcType=INTEGER}
where uid = #{uid,jdbcType=INTEGER}

mapper.select:
select uid, username, password, admin, state
from user
where uid = #{uid,jdbcType=INTEGER}
